import React, { useState } from "react";
import axios from "axios";  // Axios'u kullanarak POST isteği göndereceğiz.

function MusteriForm() {
  const [strName, setstrName] = useState('');
  const [strSurname, setstrSurname] = useState('');
  const [strMail, setstrMail] = useState('');
  const [strGSM, setstrGSM] = useState('');
  const [strAdres, setstrAdres] = useState('');

  // Form submit işlemi
  const myButtonClick = async (e) => {
    e.preventDefault();  // Formun varsayılan submit davranışını engelle

    let requestBody = {
      strName: strName,
      strSurname: strSurname,
      strMail: strMail,
      strGSM: strGSM,
      strAdres: strAdres
    };

    try {
      // API'ye POST isteği gönderiyoruz
      const response = await axios.post('https://private-d90551-merteribol.apiary-mock.com/order', requestBody, {
        headers: {
          'Content-Type': 'application/json', // JSON formatında veri gönderdiğimizi belirtiyoruz
        }
      });

      if (response.data.result === 'success') {
        alert("Giriş Başarılı");
      } else {
        alert("Hatalı işlem");
      }
    } catch (error) {
      console.error("Hata:", error);
      alert("Bir hata oluştu.");
    }
  };

  return (
    <div>
      <form onSubmit={myButtonClick}>
        <div>
          <label>Ad *</label>
          <input 
            type="text" 
            onChange={(e) => setstrName(e.target.value)} 
            value={strName} 
            placeholder="Adınızı Giriniz..."
          />
        </div>
        <div>
          <label>Soyad *</label>
          <input 
            type="text" 
            onChange={(e) => setstrSurname(e.target.value)} 
            value={strSurname} 
            placeholder="Soyadınızı Giriniz..."
          />
        </div>
        <div>
          <label>Mail Adresi *</label>
          <input 
            type="email" 
            onChange={(e) => setstrMail(e.target.value)} 
            value={strMail} 
            placeholder="adiniz@ymail.com"
          />
        </div>
        <div>
          <label>GSM</label>
          <input 
            type="text" 
            onChange={(e) => setstrGSM(e.target.value)} 
            value={strGSM} 
            placeholder="5054443322"
          />
        </div>
        <div>
          <label>Adres</label>
          <textarea 
            onChange={(e) => setstrAdres(e.target.value)} 
            value={strAdres}
          />
        </div>
        <button type="submit">Kaydet</button>
      </form>
    </div>
  );
}

export default MusteriForm;
