import React, { useState, useEffect } from "react";
import axios from "axios";
import Headermenu from "./Headermenu";
import Headertop from "./Headertop";
import Pagefooter from "./Pagefooter";
import Pagefooterinfo from "./Pagefooterinfo";
import { motion } from "framer-motion";

function MusteriForm() {
  //form data state
  const [strName, setstrName] = useState("");
  const [strSurname, setstrSurname] = useState("");
  const [strMail, setstrMail] = useState("");
  const [strGSM, setstrGSM] = useState("");
  const [strAdres, setstrAdres] = useState("");

  const [result, setResult] = useState(""); // result message for submission

  const [customer, setCustomerList] = useState([]);

  // Fetch cities data for dropdown
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://private-d90551-merteribol.apiary-mock.com/customerlist"
        );
        setCustomerList(response.data.CustomerList);
      } catch (error) {
        console.error("Müşteri verileri alınamadı:", error);
      }
    };

    fetchData();
  }, []);

  const myButtonClick = async (event) => {
    event.preventDefault(); // Prevent the form from submitting the default way

    const requestBody = {
      strName,
      strSurname,
      strMail,
      strGSM,
      strAdres
    };

    try {
      const response = await axios.post(
        "https://private-d90551-merteribol.apiary-mock.com/order",
        requestBody
      );
      if (response.data.result === "success") {
        setResult("Giriş Başarılı");
        alert("Başarılı");
      } else {
        setResult("Hatalı kullanıcı adı veya şifre");
        alert("Başarısız");
      }
    } catch (error) {
      console.error("Post işlemi başarısız:", error);
      setResult("Bir hata oluştu. Lütfen tekrar deneyin.");
    }
  };

  return (
    <div>
      {/* HEADER ve diğer içerikler */}
      <div className="page-container">
        <div className="page-content">
          <div className="container">
            <ul className="page-breadcrumb breadcrumb">
              <li>
                <a href="#">Ana Sayfa</a>
                <i className="fa fa-circle" />
              </li>
              <li>
                <a href="form_controls.html">Müşteri Yönetimi</a>
                <i className="fa fa-circle" />
              </li>
              <li className="active">Müşteri Kayıt Formu</li>
            </ul>
            <div className="row">
              <div className="col-md-12 ">
                <div className="portlet light">
                  <div className="portlet-title">
                    <div className="caption">
                      <span className="caption-subject font-green-sharp bold ">
                        Müşteri Kayıt Formu
                      </span>
                    </div>
                  </div>
                  <div className="portlet-body form">
                    <form
                      className="form-horizontal"
                      role="form"
                      onSubmit={myButtonClick} // form submit event
                    >
                      <div className="form-body">
                        {/* Ad */}
                        <div className="form-group">
                          <label className="col-md-3 control-label">Ad *</label>
                          <div className="col-md-9">
                            <input
                              type="text"
                              name="strName"
                              className="form-control input-sm"
                              placeholder="Adınızı Giriniz..."
                              onChange={(e) => setstrName(e.target.value)}
                            />
                          </div>
                        </div>

                        {/* Soyad */}
                        <div className="form-group">
                          <label className="col-md-3 control-label">Soyad *</label>
                          <div className="col-md-9">
                            <input
                              type="text"
                              name="strSurname"
                              className="form-control input-sm"
                              placeholder="Soyadınızı Giriniz..."
                              onChange={(e) => setstrSurname(e.target.value)}
                            />
                          </div>
                        </div>

                        {/* Mail */}
                        <div className="form-group">
                          <label className="col-md-3 control-label">Mail Adresi *</label>
                          <div className="col-md-9">
                            <input
                              type="email"
                              name="strMail"
                              className="form-control input-sm"
                              placeholder="adiniz@ymail.com"
                              onChange={(e) => setstrMail(e.target.value)}
                            />
                          </div>
                        </div>

                        {/* GSM */}
                        <div className="form-group">
                          <label className="col-md-3 control-label">GSM</label>
                          <div className="col-md-9">
                            <input
                              type="text"
                              name="strGSM"
                              className="form-control input-sm"
                              placeholder="5054443322"
                              onChange={(e) => setstrGSM(e.target.value)}
                            />
                          </div>
                        </div>

                        {/* Adres */}
                        <div className="form-group">
                          <label className="col-md-3 control-label">Adres</label>
                          <div className="col-md-9">
                            <textarea
                              className="form-control"
                              rows={3}
                              name="strAdres"
                              onChange={(e) => setstrAdres(e.target.value)}
                            />
                          </div>
                        </div>

                        {/* Şehir */}
                        <div className="form-group">
                          <label className="col-md-3 control-label">Şehir</label>
                          <div className="col-md-9">
                            <select className="form-control" name="cmbCity">
                              {customer.map((customer, index) => (
                                <option key={index} value={customer.sehir}>
                                  {customer.sehir}
                                </option>
                              ))}
                            </select>
                          </div>
                        </div>

                        <div className="form-actions right">
                          <button type="submit" className="btn green">
                            Kaydet
                          </button>
                        </div>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer ve diğer içerikler */}
      <motion.div className="footer-container">
        <Pagefooter />
        <Pagefooterinfo />
      </motion.div>
    </div>
  );
}

export default MusteriForm;
