import React from "react";
import Headermenu from "./Headermenu";
import Headertop from "./Headertop";
import Pagefooter from "./Pagefooter";
import Pagefooterinfo from "./Pagefooterinfo";
import { useState, useEffect } from "react";
import axios from "axios";
import { motion } from "framer-motion";

function MusteriForm() {
  //form data post işlemi//

  const [strName, setstrName] = useState();
  const [strSurname, setstrSurname] = useState();
  const [strMail, setstrMail] = useState();
  const [strGSM, setstrGSM] = useState();
  const [strAdres, setstrAdres] = useState();

  const myButtonClick = async () => {
    let requestBody = {
      strName: strName,
      strSurname: strSurname,
	  strMail:strMail,
	  strGSM:strGSM,
	  strAdres:strAdres

    };
    const response = await axios.post(
      "https://private-d90551-merteribol.apiary-mock.com/order",
      requestBody);
    if (response.data.result == "success") {
      setResult("Kayıt Başarılı");
	  alert("basarili");
    } else {
      setResult("Eksik veya yanlıs ");
	  alert("basariz");
    }
  };

  //form data post işlemi //

  //Option sehir datası get işlemi //
  const [customer, setCustomerList] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://private-d90551-merteribol.apiary-mock.com/customerlist"
        );
        setCustomerList(response.data.CustomerList);
      } catch (error) {
        console.error("Müşteri verileri alınamadı:", error);
      }
    };

    fetchData();
  }, []);
  console.log(customer);
  //Option sehir datası get işlemi //
  return (
    <div>
      {/* BEGIN HEADER */}
      <div className="page-header">
        {/* BEGIN HEADER TOP */}
        <Headertop />
        {/* END HEADER TOP */}
        {/* BEGIN HEADER MENU */}
        <div className="page-header-menu">
          <div className="container">
            {/* BEGIN MEGA MENU */}
            {/* DOC: Apply "hor-menu-light" class after the "hor-menu" class below to have a horizontal menu with white background */}
            {/* DOC: Remove data-hover="dropdown" and data-close-others="true" attributes below to disable the dropdown opening on mouse hover */}
            <div className="hor-menu ">
              <Headermenu />
            </div>
            {/* END MEGA MENU */}
          </div>
        </div>
        {/* END HEADER MENU */}
      </div>
      {/* END HEADER */}
      {/* BEGIN PAGE CONTAINER */}
      <div className="page-container">
        {/* BEGIN PAGE CONTENT */}
        <div className="page-content">
          <div className="container">
            {/* BEGIN SAMPLE PORTLET CONFIGURATION MODAL FORM*/}
            <div
              className="modal fade"
              id="portlet-config"
              tabIndex={-1}
              role="dialog"
              aria-labelledby="myModalLabel"
              aria-hidden="true"
            >
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <button
                      type="button"
                      className="close"
                      data-dismiss="modal"
                      aria-hidden="true"
                    />
                    <h4 className="modal-title">Modal title</h4>
                  </div>
                  <div className="modal-body">
                    Widget settings form goes here
                  </div>
                  <div className="modal-footer">
                    <button type="button" className="btn blue">
                      Save changes
                    </button>
                    <button
                      type="button"
                      className="btn default"
                      data-dismiss="modal"
                    >
                      Close
                    </button>
                  </div>
                </div>
                {/* /.modal-content */}
              </div>
              {/* /.modal-dialog */}
            </div>
            {/* /.modal */}
            {/* END SAMPLE PORTLET CONFIGURATION MODAL FORM*/}
            {/* BEGIN PAGE BREADCRUMB */}
            <ul className="page-breadcrumb breadcrumb">
              <li>
                <a href="#">Ana Sayfa</a>
                <i className="fa fa-circle" />
              </li>
              <li>
                <a href="form_controls.html">Müşteri Yönetimi</a>
                <i className="fa fa-circle" />
              </li>
