import React, { useState } from 'react';
import Headermenu from "./Headermenu";
import Headertop from "./Headertop";
import Pagefooter from "./Pagefooter";
import Pagefooterinfo from "./Pagefooterinfo";
import axios from "axios";
import { motion } from "framer-motion";

function MusteriForm() {
  // State Değişkenlerini Tanımla
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [gsm, setGsm] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState(1); // Şehir için varsayılan değer İstanbul
  const [result, setResult] = useState('');

  // Form gönderildiğinde çalışacak fonksiyon
  const myButtonClick = async () => {
    const requestBody = {
      service_val_name: name,
      service_val_password: surname, // Eğer şifreyi soyadı yerine kullanmak istiyorsanız
    };

    try {
      const response = await axios.post(
        'https://private-d90551-merteribol.apiary-mock.com/order',
        requestBody
      );

      if (response.data.result === "success") {
        setResult("Giriş Başarılı");
      } else {
        setResult("Hatalı kullanıcı adı veya şifre");
      }
    } catch (error) {
      setResult("Bir hata oluştu");
    }
  };

  return (
    <div>
      {/* BEGIN HEADER */}
      <div className="page-header">
        <Headertop />
        <div className="page-header-menu">
          <div className="container">
            <div className="hor-menu ">
              <Headermenu />
            </div>
          </div>
        </div>
      </div>
      {/* END HEADER */}

      {/* BEGIN PAGE CONTAINER */}
      <div className="page-container">
        <div className="page-content">
          <div className="container">
            <ul className="page-breadcrumb breadcrumb">
              <li>
                <a href="#">Ana Sayfa</a><i className="fa fa-circle" />
              </li>
              <li>
                <a href="form_controls.html">Müşteri Yönetimi</a><i className="fa fa-circle" />
              </li>
              <li className="active">Müşteri Kayıt Formu</li>
            </ul>

            <div className="row">
              <div className="col-md-12">
                <div className="portlet light">
                  <div className="portlet-title">
                    <div className="caption">
                      <span className="caption-subject font-green-sharp bold">Müşteri Kayıt Formu</span>
                    </div>
                  </div>

                  <div className="portlet-body form">
                    <form className="form-horizontal" role="form" onSubmit={(e) => e.preventDefault()}>
                      <div className="form-body">
                        <div className="form-group">
                          <label className="col-md-3 control-label">Ad *</label>
                          <div className="col-md-9">
                            <input
                              type="text"
                              className="form-control input-sm"
                              maxLength={50}
                              autoComplete="off"
                              placeholder="Adınızı Giriniz..."
                              value={name}
                              onChange={(e) => setName(e.target.value)}
                            />
                          </div>
                        </div>

                        <div className="form-group">
                          <label className="col-md-3 control-label">Soyad *</label>
                          <div className="col-md-9">
                            <input
                              type="text"
                              className="form-control input-sm"
                              maxLength={50}
                              autoComplete="off"
                              placeholder="Soyadınızı Giriniz..."
                              value={surname}
                              onChange={(e) => setSurname(e.target.value)}
                            />
                          </div>
                        </div>

                        <div className="form-group">
                          <label className="col-md-3 control-label">Mail Adresi *</label>
                          <div className="col-md-9">
                            <input
                              type="email"
                              className="form-control input-sm"
                              maxLength={50}
                              autoComplete="off"
                              placeholder="adiniz@ymail.com"
                              value={email}
                              onChange={(e) => setEmail(e.target.value)}
                            />
                          </div>
                        </div>

                        <div className="form-group">
                          <label className="col-md-3 control-label">GSM</label>
                          <div className="col-md-9">
                            <input
                              type="text"
                              className="form-control input-sm"
                              maxLength={50}
                              autoComplete="off"
                              placeholder="5054443322"
                              value={gsm}
                              onChange={(e) => setGsm(e.target.value)}
                            />
                          </div>
                        </div>

                        <div className="form-group">
                          <label className="col-md-3 control-label">Adres</label>
                          <div className="col-md-9">
                            <textarea
                              className="form-control"
                              rows={3}
                              value={address}
                              onChange={(e) => setAddress(e.target.value)}
                            />
                          </div>
                        </div>

                        <div className="form-group">
                          <label className="col-md-3 control-label">Şehir</label>
                          <div className="col-md-9">
                            <select
                              className="form-control"
                              value={city}
                              onChange={(e) => setCity(e.target.value)}
                            >
                              <option value={1}>İstanbul</option>
                              <option value={2}>Ankara</option>
                              <option value={3}>İzmir</option>
                            </select>
                          </div>
                        </div>

                        <div className="form-actions right">
                          <button type="button" className="btn green" onClick={myButtonClick}>
                            Kaydet
                          </button>
                        </div>
                      </div>
                    </form>
                    {result && <div>{result}</div>}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* BEGIN PRE-FOOTER */}
      <motion.div
        className="footer-container"
        style={{ position: "absolute", bottom: 0, left: 0, width: "100%", zIndex: 0 }}
        animate={{ y: 0 }}
        initial={{ y: 100 }}
        transition={{ type: "spring", stiffness: 300, damping: 20 }}
      >
        <Pagefooter />
        <Pagefooterinfo />
      </motion.div>
    </div>
  );
}

export default MusteriForm;
